import { Component } from '@angular/core';
import { Router } from '@angular/router';
import { cliente } from 'src/app/domain/cliente';
import { ClienteService } from 'src/app/services/cliente.service';

@Component({
  selector: 'app-cliente',
  templateUrl: './cliente.component.html',
  styleUrls: ['./cliente.component.scss']
})
export class ClienteComponent {
  cliente: cliente=new cliente();
constructor(private clienteService: ClienteService,private router: Router){
  let params = this.router.getCurrentNavigation()?.extras.queryParams;
  if(params){
    console.log(params)
    this.cliente = new cliente()
    this.cliente = params['cliente']
  }
}
guardar() {
  console.log(this.cliente)
   this.clienteService.save(this.cliente)
   this.cliente = new cliente()
 }

}
<!-- cliente.component.html -->
<div class="registro-y-listado">
  <!-- Sección de Registro de Cliente -->
  <div class="registro-cliente">
    <h2>Crear Persona</h2>
    <label for="txtCedula">Cédula:</label>
    <input id="txtCedula" [(ngModel)]="cliente.cedula" />
    <br />
    <label for="txtNombre">Nombre:</label>
    <input id="txtNombre" [(ngModel)]="cliente.nombre" />
    <br />
    <label for="txtDireccion">Dirección:</label>
    <input id="txtDireccion" [(ngModel)]="cliente.direccion" />
    <br />
    <br />
    <label for="txtTelefono">Teléfono:</label>
    <input id="txtTelefono" [(ngModel)]="cliente.telefono" />
    <br />
    <br />
    <button (click)="guardar()">Guardar</button>
  </div>

  <!-- Sección de Listado de Clientes -->
  <div class="listado-clientes">
    <h2>Listado de Clientes</h2>
    <ul>
      <li *ngFor="let cliente of cliente.lista">
        {{ cliente.nombre }} - {{ cliente.cedula }}
      </li>
    </ul>
  </div>
</div>
